import React, { useState, useEffect, createContext } from 'react';
import './App.css';
import Navbar from './Components/NavbarComponents/Navbar';
import Sidebar from './Components/SidebarComponents/Sidebar';
import TaskInterface from './Components/TaskComponents/TaskInterface';
import ShowPopupInterface from './Components/PopupComponents/ShowPopupInterface';

const UserContext = createContext();

const App = () => {

  // Theme Switcher
  const [theme, setTheme] = useState(localStorage.getItem('theme') || 'light-theme');

  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    (theme==='dark-theme')? setTheme('light-theme'):setTheme('dark-theme');
  };

  // Toggle Sidebar
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const sidebarDetails = {
    isSidebarOpen:isSidebarOpen, 
    toggleSidebar:toggleSidebar
  };

  // Popup Messages

  const [currentPopupMessage, setCurrentPopupMessage] = useState('add-task');
  const changePopupMessageBox = (msgBox) => {
    (currentPopupMessage===msgBox)?setCurrentPopupMessage(''):setCurrentPopupMessage(msgBox);
  };

  // Task Management

  const [tasks, setTasks] = useState({});
  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks'));
    (storedTasks)? setTasks(storedTasks):setTasks({});
  }, []);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);


  const getKey = (task) => {
    const key = task.date + task.time;
    return key;
  }

  const searchTask = (searchTaskName) => {
    const allTasks = Object.entries(tasks);
    const searchResult = allTasks.filter((t) => t.taskName.toLowerCase().startsWith(searchTaskName.toLowerCase()));
    return searchResult;
  }

  const addTask = (task) => {
    const key = getKey(task);
    const updatedTasks = {...tasks, [key] : task};
    setTasks(updatedTasks);
  };

  const updateTask = (task, newTask) => {
    const key = getKey(task);
    const updatedTasks = {...tasks, [key] : newTask};
    setTasks(updatedTasks);
  };

  const deleteTask = (task) => {
    const key = getKey(task);
    const updatedTasks = {...tasks};
    delete updatedTasks[key];
    setTasks(updatedTasks);
  };

  const clearAllTasks = () => {
    setTasks({});
  };

  return (
    <div className={'App ' + theme}>
      <UserContext.Provider>
        <Navbar toggleTheme={toggleTheme} sidebarDetails={sidebarDetails}/>
        <Sidebar sidebarDetails={sidebarDetails}/>
        <TaskInterface />
        {
          currentPopupMessage && 
          <ShowPopupInterface 
            currentPopupMessage={currentPopupMessage}
            changePopupMessageBox={changePopupMessageBox}
          />
        }
      </UserContext.Provider>
    </div>
  );
};

export default App;
