import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './Components/Navbar';

const App = () => {

  // Theme Switcher
  const [theme, setTheme] = useState('dark-theme');

  useEffect(() => {
    const storedTheme = localStorage.setItem('theme', theme);
  }, []);

  useEffect(() => {
    localStorage.setItem('theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    theme? setTheme(''):setTheme('dark-theme');
  };

  // Task Management
  const [tasks, setTasks] = useState({});
  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem('tasks'));
    (storedTasks)? setTasks(storedTasks):setTasks({});
  }, []);

  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);


  const getKey = (task) => {
    const key = task.date + task.time;
    return key;
  }

  const addTask = (task) => {
    const key = getKey(task);
    const updatedTasks = {...tasks, [key] : task};
    setTasks(updatedTasks);
  };

  const updateTask = (task, newTask) => {
    const key = getKey(task);
    const updatedTasks = {...tasks, [key] : newTask};
    setTasks(updatedTasks);
  };

  const deleteTask = (task) => {
    const key = getKey(task);
    const updatedTasks = {...tasks};
    delete updatedTasks[key];
    setTasks(updatedTasks);
  };

  const clearAllTasks = () => {
    setTasks({});
  };

  return (
    <div className={'App ' + theme}>
      <Navbar toggleTheme={toggleTheme} />
    </div>
  );
};

export default App;
