import React from 'react';
import '../../Styles/ShowPopupForm.css';


const ShowPopupForm = () => {
  return (
    <form className='popupform_container'>
      <div className='popup_input'>
        <label htmlFor="taskname_input">Task Name</label>
        <input type="text" name="task_name" id="taskname_input"/>
      </div>
      <div className='popup_input'>
        <label htmlFor="taskenddate_input">End Date</label>
        <input type="date" name="task_end_date" id="taskenddate_input"/>
      </div>
      <div className='popup_input'>
        <label htmlFor="taskendtime_input">End Time</label>
        <input type="time" name="task_end_time" id="taskendtime_input"/>
      </div>
      <div className='popup_checkbox'>
        <label htmlFor="taskfavourite_input">Mark as fa</label>
        <input type="checkbox" name="favorite_task" id="taskfavourite_input"/>
      </div>
      <button type="submit" id="popupform_submit_btn" className='popup_btn'>Confirm</button>
    </form>
  )
};

export default ShowPopupForm;
