import React, {useState} from 'react';
import '../../Styles/TaskButton.css';

const TaskButton = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className='task_dropdown'>
      <button className='task_dropdown_button' onClick={toggleDropdown}>
        <svg className='task_button_svg' xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor">
          <path stroke-linecap="round" stroke-linejoin="round" d={props.pathD} />
        </svg>
        <p className='task_button_text'>{props.buttonName}</p>
      </button>
      {
        isOpen &&
        (<div className="dropdown_content">
          {
            props.buttonContents.map((opt) => {
              <div key={opt} className="custom-dropdown-option" >
                {opt}
              </div>
            })
          }
        </div>)
      }
    </div>
  )
};

export default TaskButton;
