import React, {useState} from 'react';
import {useAllContext} from '../../App';
import '../../Styles/ShowPopupBox.css';


const ShowPopupForm = () => {
  const StatesAndFunctions = useAllContext();
  const popupSelectedTaskDetails = StatesAndFunctions.currentSelectedTaskDetails;
  const [formTaskName, setFormTaskName] = useState(popupSelectedTaskDetails ? popupSelectedTaskDetails.taskName : '');
  const [formTaskEndDate, setFormTaskEndDate] = useState(popupSelectedTaskDetails ? popupSelectedTaskDetails.taskEndDate : '');
  const [formTaskEndTime, setFormTaskEndTime] = useState(popupSelectedTaskDetails ? popupSelectedTaskDetails.taskEndTime : '');
  const [formTaskPriority, setFormTaskPriority] = useState(popupSelectedTaskDetails ? popupSelectedTaskDetails.taskPriority : false);

  const getDateFormat = (d) => {
    d = d.split('-');
    return d[2].padStart('0',4)+'-'+d[1].padStart('0',2)+'-'+d[0].padStart('0',2);
  };

  const getTimeFormat = (t) => {
    t = t.split(':');
    return t[0].padStart('0',2)+':'+t[1].padStart('0',2)+':'+(t[2]?t[2].padStart('0',2):'00');
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent default form submission behavior

    // Prepare task object
    const task = {
      taskName:formTaskName,
      taskEndDate:formTaskEndDate,
      taskEndTime:f,
      taskPriority: taskPriority ? "true" : "false"
    };

    // if (currentSelectedTaskDetails) {
    //   updateTask(currentSelectedTaskDetails, task);
    // } else {
    //   addTask(task);
    // }
    setTaskName('');
    setTaskEndDate('');
    setTaskEndTime('');
    setTaskPriority(false);
  };

  return (
    <div className='popup_container_wrapper'>
      <h2 className='popup_container_header'>{popupSelectedTaskDetails?"Update Task":"Add Task"}</h2>
      <form className='popup_container_inner_wrapper'>
      <div className='popup_inner_boxes'>
          <label htmlFor="taskname_input">Task Name</label>
          <input type="text" name="task_name" id="taskname_input" placeholder='Task name' value={popupSelectedTaskDetails.taskName} onChange={(e) => setTaskName(e.target.value)}/>
      </div>
      <div className='popup_inner_boxes'>
          <label htmlFor="taskenddate_input">End Date</label>
          <input type="date" name="task_end_date" id="taskenddate_input" value={getDateFormat(popupSelectedTaskDetails.taskEndDate)} />
      </div>
      <div className='popup_inner_boxes'>
          <label htmlFor="taskendtime_input">End Time</label>
          <input type="time" name="task_end_time" id="taskendtime_input" value={getTimeFormat(popupSelectedTaskDetails.taskEndTime)}/>
      </div>
      <div className='popup_checkbox'>
          <label htmlFor="taskfavourite_input">Mark as favorite</label>
          <input type="checkbox" name="favorite_task" id="taskfavourite_input" checked={popupSelectedTaskDetails.taskPriority=="true"} />
      </div>
      <button type="submit" id="popupform_submit_btn" className='popup_btn' onClick={handleSubmit}>{popupSelectedTaskDetails?"Edit":"Save"}</button>
      </form>
    </div>
  )
};

export default ShowPopupForm;
